import React, { Component } from 'react'
import { Box, FormControlLabel, Typography, RadioGroup, Radio, Select, MenuItem, Checkbox } from '@material-ui/core';
import TextField from './TextField';

const styles = {
    container: {
        display: "flex",
        width: '100%',
        heigth: '100%',
        marginTop: 10
    },
    label: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
        fontFamily: "sans-serif"
    },
    textfield: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end"
    },
    radioBox: {
        width: '100%',
        display: 'flex'
    },
    radioGroup: {
        flex: 1,
        display: "flex",
        flexDirection: "row"
    }
}
export default class FormComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            anchorEl: null,
            SelectedValue: "",
            checked: [],

        }
    }
    _returnData = (data) => {
        const { published, values, label, type } = data;
        switch (type) {
            case "Textfield":
                return this._TextField(values, published)
            case "Radiobutton":
                return this._Radiobutton(values, published, label)
            case "Select":
                return this._dropDown(values, published)
            case "Checkbox":
                return this._checkBox(values, published)
            default:
                return <TextField published={published} />
        }

    }

    _TextField = (values, published) => {
        return (
            <TextField published={published} />
        )
    }

    _dropDown = (values, published) => {
        let { anchorEl, SelectedValue } = this.state;
        let arr = []
        values.forEach((item) => {
            arr.push(
                <MenuItem value={item} onClick={this.handleClose}>{item}</MenuItem>
            )
        })
        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF" }}>

            <Select
                labelId="demo-controlled-open-select-label"
                id={"demo-controlled-open-select"}
                open={anchorEl}
                onClose={this.handleClose}
                onOpen={this.handleClick}
                style={{ width: '100%' }}
                onChange={this.handleChange}
                value={SelectedValue}
            >
                {arr}
            </Select>
        </Box>
    }

    _Radiobutton = (values, published, label) => {
        let arr = [];
        let coordsField = false;

        label === "Koordinaten des Projektstandortes (rechts/links)" ?
            coordsField = true
            : coordsField = false

        values.forEach((item) => {
            arr.push(
                <Box style={{ flex: 1 }}><FormControlLabel
                    value={item}
                    control={<Radio color="primary" value={item} onClick={coordsField ? this._coordsField : this._radioButtonField} />}
                    label={<Typography style={{ fontSize: 12 }}>{item}</Typography>}
                    labelPlacement="End"
                    style={{ paddingLeft: 10, paddingTop: 1 }}
                />
                    {coordsField ?
                        <div>
                            <Select
                                labelId="demo-controlled-open-select-label"
                                id={"demo-controlled-open-select"}
                                open={this.state.anchorEl}
                                onClose={this.handleClose}
                                onOpen={this.handleClick}
                                style={{ width: '100%' }}
                                onChange={this.handleChange}
                                value={this.state.SelectedValue}
                            >
                                <MenuItem value={item} onClick={this.handleClose}>{"Bitte Ausfüllen"}</MenuItem>
                                <MenuItem value={item} onClick={this.handleClose}>{"2"}</MenuItem>
                                <MenuItem value={item} onClick={this.handleClose}>{"3"}</MenuItem>
                            </Select>
                            <TextField published={published} /></div>
                        : null}</Box>
            )
        })

        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF" }}>
            <RadioGroup row aria-label="position" style={styles.radioGroup}>
                {arr}
            </RadioGroup>
        </Box>

    }

    _checkBox = (values, published) => {
        let arr = []
        values.forEach((item) => {
            arr.push(
                <Box style={{ flex: 1 }}>
                    <FormControlLabel
                        value={item}
                        control={<Checkbox color="primary" />}
                        label={<Typography style={{ fontSize: 12 }}>{item}</Typography>}
                        labelPlacement="End"
                        style={{ paddingLeft: 10, paddingTop: 1 }}
                    /></Box>
            )
        })
        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF", justifyContent: "space-evenly" }}>
            {arr}
        </Box>
    }

    _coordsField = (item) => {
        let value = item.target.value;
    }

    _radioButtonField = (item) => {
        let value = item.target.value;
    }

    handleClose = () => {
        this.setState({ anchorEl: null })
    }

    handleClick = () => {
        this.setState({ anchorEl: true })
    }
    handleChange = (item) => {
        console.log(item);
        this.setState({ SelectedValue: item.target.value })
    }



    render() {
        const data = this.props.data;
        const { label } = this.props.data;
        const { key } = this.props;
        return (
            <Box style={styles.container} key={key}>
                <Box style={styles.label}>
                    {label}
                </Box>
                <Box style={styles.textfield}>
                    {this._returnData(data)}
                </Box>
            </Box>
        )
    }
}
