import React, { Component } from 'react'
import HeaderLogos from './components/HeaderLogos';
import Header from './components/Header';
import Form from './sites/Form';
import Site2 from './sites/Site2';
import { Box, Grid } from '@material-ui/core';
import Table from './components/Table';
const styles = {
  container: {
    height: '100vh',
    width: '100%'
  },
  form: {
    height: '100%', width: '100%'
  },
  rightPage: {
    height: '100%',
    width: '100%'
  }
}
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allData: {}
    }
  }
  _changeText(data) {
    let { allData } = this.state;
    let { id, value, label } = data;
    allData[id] = {
      value: value,
      label: label
    }
    this.setState({ allData })
  }

  _RadioBtn(data) {
    alert(data.value)
  }
  render() {

    return (
      <Box style={styles.container}>
        <HeaderLogos />
        <Header />
        <Grid container spacing={3} style={{}}>
          <Grid item xs={12} sm={6}>
            <Box style={styles.form}><Form RadioBtn={this._RadioBtn.bind(this)} changeText={this._changeText.bind(this)} /></Box>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Box style={styles.rightPage}><Site2 /></Box>
          </Grid>
        </Grid>
        <Box style={{ marginTop: 20 }}>
          <Table />
        </Box>
      </Box>
    )
  }
}
