import React, { Component } from 'react';
import { Button } from '@material-ui/core';
export default class index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }
  componentDidMount() {
    this.showData();
  }

  async showData() {
    await fetch('http://localhost:5000/api/v1/eintraege').then((data) =>
      (data = data.json()).then((data) => {
        this.setState({ data: data.data });
        console.log(data.data);
      })
    );
  }

  getItems = (item) => {
    return (
      <Button
        style={{
          height: 100,
          width: 500,
          borderColor: 'lightgreen',
          borderStyle: 'solid',
          margin: 20,
          borderWidth: 0.5,
          borderRadius: 10,
          overflow: 'hidden',
        }}
      >
        <p>{item.CompanyContact}</p>
        {'\n'}
        <p>{item.Email}</p> {'\n'}
        <p>{item.Name}</p> {'\n'}
        <p>{item.InvolvedCompanies}</p> {'\n'}
      </Button>
    );
  };
  render() {
    let { data } = this.state;
    return (
      <div style={{ display: 'flex', flexDirection: 'column' }}>
        {data.map((item) => this.getItems(item))}
      </div>
    );
  }
}
