import React, { Component } from 'react';
import { Box, Button, Tab, Tabs, AppBar } from '@material-ui/core';
import ServerUtils from '../../../utils/ServerUtils';
import TabHandler from '../TabHandler';
const styles = {
  email: {
    justifyContent: 'flex-start',
    flex: 1,
    display: 'flex',
    alignItems: 'flex-end',
  },
  logoutBtn: {
    justifyContent: 'flex-end',
    flex: 1,
    display: 'flex',
  },
  appBar: {
    alignItems: 'center',
    justifyContent: 'center',
    display: 'flex',
    flexDirection: 'row',
    backgroundColor: 'rgb(106, 172, 69)',
  },
};

export default class index extends Component {
  constructor(props) {
    super(props);
    this.ServerUtils = new ServerUtils();
    this.state = {
      value: 0,
      phoneSize: false,
    };
  }

  componentDidMount() {
    this.checkIfPhone();
  }

  checkIfPhone = () => {
    let { phoneSize } = this.state;
    if (window.innerWidth < 699) {
      phoneSize = true;
    }
    this.setState({ phoneSize });
  };

  _Logout = async () => {
    await this.ServerUtils.logout();
  };

  _Login = () => {
    this.props.pressedLogin();
  };

  handleChange = (event, newValue) => {
    this.setState({ value: newValue });
  };

  render() {
    let { username, userRole, fnb } = this.props;
    let { value, phoneSize } = this.state;

    return (
      <>
        {phoneSize ? null : (
          <AppBar position="static" style={styles.appBar}>
            <Tabs
              orientation="vertical"
              variant="scrollable"
              style={{
                width: '100%',
                display: 'flex',
                overflow: 'hidden',
              }}
              value={value}
              onChange={this.handleChange}
            >
              <Tab label="Einleitung" />
              <Tab label="Formular" />
              <Tab label="Anleitung" />

              {userRole === 'user' || userRole === 'admin' ? (
                <Tab label="Verwaltung" />
              ) : null}
            </Tabs>
            {username ? (
              <Box
                style={{
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  display: 'flex',
                }}
              >
                <Box style={styles.email}>
                  <p
                    style={{
                      fontFamily: 'sans-serif',
                      fontSize: 14,
                      margin: 10,
                    }}
                  >
                    {phoneSize ? null : username}
                  </p>
                </Box>
                <Box style={styles.logoutBtn}>
                  <Button onClick={this._Logout}>Logout</Button>
                </Box>
              </Box>
            ) : (
              <Box style={styles.logoutBtn}>
                <Button onClick={this._Login}>Login</Button>
              </Box>
            )}
          </AppBar>
        )}

        <TabHandler value={value} index={0} fnb={fnb} />
      </>
    );
  }
}
