import React, { Component } from 'react'
import { TextField, Box, FormControlLabel, Typography, RadioGroup, Radio, Select, MenuItem, FormControl, Checkbox, FormGroup } from '@material-ui/core';

const styles = {
    container: {
        display: "flex",
        width: '100%',
        heigth: '100%',
        marginTop: 10
    },
    label: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
        fontFamily: "sans-serif"
    },
    textfield: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end"
    },
    radioBox: {
        width: '100%',
        display: 'flex'
    },
    radioGroup: {
        flex: 1,
        display: "flex",
        flexDirection: "row"
    }
}
export default class FormComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            anchorEl: null,
            SelectedValue: "",
            checked: [],
            allData: []
        }
    }
    _returnData = (data) => {
        const { published, values, label, type } = data;
        let { allData } = this.state;
        allData.push({
            label: ""
        })
        this.setState({ allData })
        switch (type) {
            case "Textfield":
                return <TextField style={{ width: '100%', backgroundColor: published ? "#FFE4C470" : "white" }} />
            case "Radiobutton":
                return this._Radiobutton(values, published, label)
            case "Select":
                return this._dropDown(values, published)
            case "Checkbox":
                return this._checkBox(values, published)
            default:
                return <TextField style={{ width: '100%', backgroundColor: published ? "#FFE4C470" : "white" }} />
        }

    }

    setRadioButton = (data) => {
        alert(data.target.value);
        /* let { checked } = this.state;
        checked.push(
            label = !this.state.checked[label]
        )
        this.setState({ checked }) */
    }

    handleClose = () => {
        this.setState({ anchorEl: null })
    }

    handleClick = () => {
        this.setState({ anchorEl: true })
    }
    handleChange = (item) => {
        console.log(item);
        this.setState({ SelectedValue: item.target.value })
    }
    _dropDown = (values, published) => {
        let { anchorEl, SelectedValue } = this.state;
        let arr = []
        values.forEach((item) => {
            arr.push(
                <MenuItem value={item} onClick={this.handleClose}>{item}</MenuItem>
            )
        })
        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF" }}>

            <Select
                labelId="demo-controlled-open-select-label"
                id="demo-controlled-open-select"
                open={anchorEl}
                onClose={this.handleClose}
                onOpen={this.handleClick}
                style={{ width: '100%' }}
                onChange={this.handleChange}
                value={SelectedValue}
            >
                {arr}
            </Select>
        </Box>
    }

    _Radiobutton = (values, published, label) => {
        let arr = [];
        let { checked } = this.state;
        values.forEach((item) => {
            arr.push(
                <Box style={{ flex: 1 }}><FormControlLabel
                    value={item}
                    control={<Radio color="primary" checked={this.state.checked[label]} value={item} onClick={this.setRadioButton} />}
                    label={<Typography style={{ fontSize: 12 }}>{item}</Typography>}
                    labelPlacement="End"
                    style={{ paddingLeft: 10, paddingTop: 1 }}
                />
                    {label === "Koordinaten des Projektstandortes (rechts/links)" ?
                        <TextField placeholder={item} />
                        : null}</Box>
            )
        })

        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF" }}>
            <RadioGroup row aria-label="position" style={styles.radioGroup}>
                {arr}
            </RadioGroup>
        </Box>

    }

    _checkBox = (values, published) => {
        let arr = []
        values.forEach((item) => {
            arr.push(
                <Box style={{ flex: 1 }}>
                    <FormControlLabel
                        value={item}
                        control={<Checkbox color="primary" />}
                        label={<Typography style={{ fontSize: 12 }}>{item}</Typography>}
                        labelPlacement="End"
                        style={{ paddingLeft: 10, paddingTop: 1 }}
                    /></Box>
            )
        })
        return <Box border={1} borderColor="grey.500" style={{ width: '100%', display: 'flex', backgroundColor: published ? "#FFE4C470" : "#FFFFFF", justifyContent: "space-evenly" }}>
            {arr}
        </Box>
    }

    render() {
        const data = this.props.data;
        const { label } = this.props.data;
        const { key } = this.props;
        return (
            <Box style={styles.container} key={key}>
                <Box style={styles.label}>
                    {label}
                </Box>
                <Box style={styles.textfield}>
                    {this._returnData(data)}
                </Box>
            </Box>
        )
    }
}
