import React, { Component } from 'react'
import { TextField, Box, Checkbox } from '@material-ui/core';

const styles = {
    container: {
        display: "flex",
        width: '100%',
        heigth: '100%',
        marginTop: 10
    },
    label: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
        fontFamily: "sans-serif"
    },
    textfield: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
    }
}
export default class FormComponent extends Component {

    _checkBox = (values) => {
        let arr = []
        values.forEach((item) => {
            arr.push(
                <Box style={{ display: "flex", width: '100%', height: '100%' }}>
                    <Box style={{ flex: 1, alignItems: "flex-end", display: "flex" }}>{item}</Box>
                    <Box style={{ flex: 1 }}><Checkbox /></Box>
                </Box>
            )
        })
        return arr
    }
    render() {
        const { label, published, isCheckBox, checkBoxValues } = this.props.data;
        const { key } = this.props;
        return (
            <Box style={styles.container} key={key}>
                <Box style={styles.label}>
                    {label}
                </Box>
                <Box style={styles.textfield}>
                    {isCheckBox ? this._checkBox(checkBoxValues) : <TextField style={{ width: '100%', backgroundColor: published ? "#FFE4C470" : "white" }} />}

                </Box>
            </Box>
        )
    }
}
