import React, { Component } from 'react'
import data from '../data/Data';
import { Box } from '@material-ui/core';
import FormComponent from './../components/FormComponent';

const styles = {
    container: {
        height: '100%',
        width: '100%'
    },
    header: {
        backgroundColor: "rgb(106, 172, 69)",
        height: 40,
        justifyContent: "center",
        alignItems: "center",
        alignContent: "center",
        width: '100%',
        display: "flex",
        marginTop: 30,
        marginBottom: 30
    }
}
export default class Form extends Component {
    _changeText(data) {
        this.props.changeText(data)
    }

    _getTextFields = () => {
        let arr = [];
        for (const [id, value] of Object.entries(data)) {
            if (id === "reported") {
                arr.push(<Box key={`${id}Box`} style={styles.header}><h3 style={{ fontFamily: "sans-serif", color: "white" }}>Informationen zum Projekt</h3></Box>)
            }
            arr.push(<FormComponent setAllData={this.props.setAllData} key={id} data={value} />);
        }
        return arr;
    }
    render() {
        return (
            <Box style={styles.container}>
                <Box style={styles.header}><h3 style={{ fontFamily: "sans-serif", color: "white" }}>Ansprechpartnerin bzw. Ansprechpartner</h3></Box>
                {this._getTextFields()}
            </Box>

        )
    }
}
