import React, { Component } from 'react';
import { Box, Button, Tab, Tabs, TabPanel, AppBar } from '@material-ui/core';

const styles = {
  container: {
    width: '100%',
    backgroundColor: 'grey',
    height: 60,
  },
  dataContainer: {
    display: 'flex',
    flexDirection: 'row',
    height: '100%',
  },
  email: {
    justifyContent: 'flex-start',
    flex: 1,
    display: 'flex',
    alignItems: 'flex-end',
  },
  logoutBtn: {
    justifyContent: 'flex-end',
    flex: 1,
    display: 'flex',
  },
};
export default class index extends Component {
  _Logout = async () => {
    await fetch('http://localhost:5000/api/v1/auth/logout').then((data) =>
      (data = data.json()).then((data) => console.log(data))
    );
    document.cookie = 'token=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
    localStorage.clear();
    window.location.href = '/';
  };

  a11yProps(index) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  render() {
    let { username } = this.props;
    return (
      <Box style={styles.container}>
        <Box>
          <AppBar position="static">
            <Tabs
              /* value={value}
              onChange={handleChange} */
              aria-label="simple tabs example"
            >
              <Tab label="Item One" {...this.a11yProps(0)} />
              <Tab label="Item Two" {...this.a11yProps(1)} />
              <Tab label="Item Three" {...this.a11yProps(2)} />
            </Tabs>
          </AppBar>
          <TabPanel /* value={value} */ index={0}>Item One</TabPanel>
          <TabPanel /* value={value} */ index={1}>Item Two</TabPanel>
          <TabPanel /*  value={value} */ index={2}>Item Three</TabPanel>
        </Box>
        {username ? (
          <Box style={styles.dataContainer}>
            <Box style={styles.email}>
              <p style={{ fontFamily: 'sans-serif', fontSize: 15, margin: 10 }}>
                {username}
              </p>
            </Box>
            <Box style={styles.logoutBtn}>
              <Button onClick={this._Logout}>Logout</Button>
            </Box>
          </Box>
        ) : null}
      </Box>
    );
  }
}
