import React, { Component } from 'react';
import { Button, Box } from '@material-ui/core';
import { ChevronRightOutlined } from '@material-ui/icons';

const styles = {
  container: {},
  box: {
    flex: 1,
    flexDirection: 'row',
    display: 'flex',
    flexWrap: 'wrap',
  },
};
export default class index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      pressedData: null,
    };
  }
  componentDidMount() {
    this.showData();
  }

  async showData() {
    await fetch('http://localhost:5000/api/v1/eintraege').then((data) =>
      (data = data.json()).then((data) => {
        this.setState({ data: data.data });
      })
    );
  }

  pressedContact = (item) => {
    this.setState({ pressedData: item });
  };

  getItems = (item) => {
    return (
      <Button
        style={styles.container}
        onClick={() => this.pressedContact(item)}
      >
        <Box style={styles.box}>
          <p style={{ flex: 1 }}>1{item.CompanyContact}</p>
          <p style={{ flex: 1 }}>2{item.Email}</p>
          <p style={{ flex: 1 }}>3{item.Name}</p>
          <p style={{ flex: 1 }}>4{item.CompanyContact}</p>
          <p style={{ flex: 1 }}>1{item.CompanyContact}</p>
          <p style={{ flex: 1 }}>2{item.Email}</p>
          <p style={{ flex: 1 }}>3{item.Name}</p>
          <p style={{ flex: 1 }}>4{item.CompanyContact}</p>
        </Box>
        <ChevronRightOutlined />
      </Button>
    );
  };
  render() {
    let { data, pressedData } = this.state;
    return (
      /*     <Box style={{ display: 'flex', flexDirection: 'row' }}>
        <Box
          style={{
            display: 'flex',
            flexDirection: 'column',
            flex: 1,
          }}
        >
          {data.map((item) => this.getItems(item))}
        </Box>
        {pressedData ? (
          <Box style={{ flex: 1, margin: 20, width: '60%' }}>
            {JSON.stringify(pressedData)}}
          </Box>
        ) : null}
      </Box> */
      <Box style={{ display: 'flex', flexDirection: 'row' }}>
        <Box
          style={{
            flex: 1,
          }}
        >
          {data.map((item) => this.getItems(item))}
        </Box>
        {pressedData ? (
          <Box style={{ flex: 1 }}>{JSON.stringify(pressedData)}</Box>
        ) : null}
      </Box>
    );
  }
}
