import React, { Component } from 'react';
import { Menu, MenuItem, IconButton } from '@material-ui/core';

import { List } from '@material-ui/icons';

const styles = {
  email: {
    justifyContent: 'flex-start',
    flex: 1,
    display: 'flex',
    alignItems: 'flex-end',
  },
  logoutBtn: {
    justifyContent: 'flex-end',
    flex: 1,
    display: 'flex',
  },
  appBar: {
    width: 100,
    position: 'absolute',
    backgroundColor: 'rgb(106, 172, 69)',
  },
};

export default class PhoneSize extends Component {
  constructor(props) {
    super(props);
    this.state = {
      anchorEl: false,
    };
  }

  handleClick = (event) => {
    this.setState({ anchorEl: !this.state.anchorEl });
  };

  render() {
    let { anchorEl } = this.state;
    let { username, userRole, fnb, value, phoneSize } = this.props;
    return (
      <>
        <IconButton onClick={this.handleClick} children={<List />} />
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          style={{ position: 'absolute', top: 0 }}
        >
          <MenuItem onClick={this.handleClick}>Einleitung</MenuItem>
          <MenuItem onClick={this.handleClick}>Formular</MenuItem>
          <MenuItem onClick={this.handleClick}>Anleitung</MenuItem>

          {userRole === 'user' || userRole === 'admin' ? (
            <MenuItem label="Verwaltung" />
          ) : null}
        </Menu>
      </>
    );
  }
}
