import React, { Component } from 'react'
import { Box, TextField } from '@material-ui/core';

const styles = {
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        width: 300,
        margin: 100,
    },
    //style for font size
    resize: {
        fontSize: 50
    },
}

export default class DescriptionField extends Component {
    render() {
        return (
            <Box style={{ width: '100%', height: '100%', backgroundColor: "red" }}>
                <TextField label="input label name here"
                    margin="normal"
                    inputProps={{ style: { fontSize: 40 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 40 } }} // font size of input label
                />
            </Box>

        )
    }
}
