import React, { Component } from 'react'
import { Box, TextField } from '@material-ui/core';
import { withStyles } from "@material-ui/core/styles";
const styles = {
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        width: 300,
        margin: 100,
    },
    //style for font size
    resize: {
        fontSize: 50
    },
    input1:{
        height:10
    }
}

export default class DescriptionField extends Component {
    render() {
        return (
            <Box style={{ width: '100%', height: '100%', backgroundColor: "red" }}>
                <TextField
                    fullWidth={true}
                    style={{ height: 0 }}
                    InputProps={{ classes: { input: this.input1 } }}
                />
            </Box>

        )
    }
}
