import React, { Component } from 'react'
import { TextField, Box, Checkbox, FormControlLabel } from '@material-ui/core';

const styles = {
    container: {
        display: "flex",
        width: '100%',
        heigth: '100%',
        marginTop: 10
    },
    label: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
        fontFamily: "sans-serif"
    },
    textfield: {
        flex: 1,
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "flex-end",
    },
    checkbox: {
        width: '100%',
        height: '100%',
        display: "flex",
        justifyContent: "space-between"
    }
}
export default class FormComponent extends Component {
    _returnData = (data) => {
        const { published, values } = data;
        switch (data.type) {
            case "Textfield":
                return <TextField style={{ width: '100%', backgroundColor: published ? "#FFE4C470" : "white" }} />
            case "Checkbox":
                return this._checkBox(values)
            default:
                return null
        }
    }

    _checkBox = (values) => {
        let arr = []
        values.forEach((item) => {
            arr.push(
                <Box style={{ flex: 1 }}><FormControlLabel
                    value={item}
                    control={<Checkbox color="primary" />}
                    label={item}
                    labelPlacement="Start"
                /></Box>
            )
        })
        return <Box border={1} borderColor="grey.500" style={styles.checkbox}>{arr}</Box>
    }
    render() {
        const data = this.props.data;
        const { label } = this.props.data;
        const { key } = this.props;
        return (
            <Box style={styles.container} key={key}>
                <Box style={styles.label}>
                    {label}
                </Box>
                <Box style={styles.textfield}>
                    {this._returnData(data)}
                </Box>
            </Box>
        )
    }
}
