import React from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

class SimpleMenu extends React.Component {
  state = {
    anchorEl: false,
  };

  handleClick = (event) => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { anchorEl } = this.state;

    return (
      <div>
        <IconButton onClick={this.handleClick} children={<List />} />
        <Menu anchorEl={anchorEl} open={Boolean(anchorEl)}>
          <MenuItem onClick={this.handleClick}>Einleitung</MenuItem>
          <MenuItem onClick={this.handleClick}>Formular</MenuItem>
          <MenuItem onClick={this.handleClick}>Anleitung</MenuItem>
        </Menu>
      </div>
    );
  }
}

export default SimpleMenu;
