class ServerUtils {
  login = async (body) => {
    const data = await fetch('http://localhost:5000/api/v1/auth/login', {
      method: 'POST',
      credentials: 'include', // Don't forget to specify this if you need cookies
      body: JSON.stringify(body),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    return JSON.stringify(data);
  };
  logout = async () => {
    await fetch('http://localhost:5000/api/v1/auth/logout').then((data) =>
      (data = data.json()).then((data) => console.log(data))
    );
    document.cookie = 'token=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
    localStorage.clear();
    window.location.href = '/';
  };
  sendData = async (data) => {
    await fetch('http://localhost:5000/api/v1/eintraege', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((data) => (data = data.json()))
      .then((data) => console.log(data));
  };
}
module.exports = ServerUtils;
