import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
const styles = {
  text: {
    fontSize: 18,
    fontFamily: 'sans-serif',
  },
  coloredHeader: {
    color: 'rgb(106, 172, 69)',
  },
  header: {
    backgroundColor: 'rgb(106, 172, 69)',
    color: 'white',
    fontSize: 15,
    fontWeight: 'bold',
    padding: 1,
  },
  names: {
    fontSize: 15,
    fontWeight: 'bold',
    padding: 1,
  },
};
export default class index extends Component {
  /* 
 stefanie.jacobi@bayernets.de
hagen.kropp@ferngas.de 
alessandro.brunoni@fluxys.com 
alessandro.brunoni@fluxys.com 
nils.melcher@gascade.de
tammo.dunkhase@gtg-nord.de
thorsten.bruemmer@gasunie.de
susan.valtin@grtgaz-deutschland.de
info@lbtg.de
nils.melcher@gascade.de
m.jones@nowega.de
rene.doering@ontras.com
nils.melcher@gascade.de
gastransport@oge.net
anitha.prasad@terranets-bw.de
jan.eisenberg@thyssengas.com
  */
  render() {
    let rows = [
      {
        fnb: 'bayernets GmbH',
        partner: 'Stefanie Jacobi',
        email: 'stefanie.jacobi@bayernets.de',
      },
      {
        fnb: 'Ferngas Netzgesellschaft mbh',
        partner: 'Hagen Kropp',
        email: 'hagen.kropp@ferngas.de',
      },
      {
        fnb: 'Fluxys Deutschland GmbH',
        partner: 'Alessandro Brunoni',
        email: 'alessandro.brunoni@fluxys.com',
      },
      {
        fnb: 'Fluxys TENP GmbH',
        partner: 'Alessandro Brunoni',
        email: 'alessandro.brunoni@fluxys.com',
      },
      {
        fnb: 'GASCADE Gastransport GmbH',
        partner: 'Nils Melcher',
        email: 'nils.melcher@gascade.de',
      },
      {
        fnb: 'Gastransport Nord GmbH',
        partner: 'Tammo Dunkhase',
        email: 'tammo.dunkhase@gtg-nord.de',
      },
      {
        fnb: 'Gasunie Deutschland Transport Services GmbH',
        partner: 'Thorsten Brümmer',
        email: 'thorsten.bruemmer@gasunie.de',
      },
      {
        fnb: 'GRTgaz Deutschland GmbH',
        partner: 'Susan Valtin',
        email: 'susan.valtin@grtgaz-deutschland.de',
      },
      {
        fnb: 'Lubmin-Brandov Gastransport GmbH',
        partner: 'Marion Abel',
        email: 'info@lbtg.de',
      },
      {
        fnb: 'NEL Gastransport GmbH',
        partner: 'Nils Melcher',
        email: 'nils.melcher@gascade.de',
      },
      {
        fnb: 'Nowega GmbH',
        partner: 'Martin Jones',
        email: 'm.jones@nowega.de',
      },
      {
        fnb: 'ONTRAS Gastransport GmbH',
        partner: 'René Döring',
        email: 'rene.doering@ontras.com',
      },
      {
        fnb: 'OPAL Gastransport GmbH & Co. KG',
        partner: 'Nils Melcher',
        email: 'nils.melcher@gascade.de',
      },
      {
        fnb: 'Open Grid Europe GmbH',
        partner: 'Thomas Stang',
        email: 'gastransport@oge.net',
      },
      {
        fnb: 'terranets bw GmbH',
        partner: 'Anitha Prasad',
        email: 'anitha.prasad@terranets-bw.de',
      },
      {
        fnb: 'Thyssengas GmbH',
        partner: 'Jan Eisenberg',
        email: 'jan.eisenberg@thyssengas.com',
      },
    ];
    return (
      <div style={{ margin: 10 }}>
        {' '}
        <h1 style={{ fontFamily: 'sans-serif' }}>Anleitung</h1>
        <p style={styles.text}>
          <h3 style={styles.coloredHeader}>Kurze Erläuterung</h3>
          Dieses Tabellenblatt gibt Hilfestellungen für das korrekte Ausfüllen
          des Formulars zur Marktabfrage. Sollten Sie weiterhin offene Fragen
          haben, wenden Sie sich bitte an die genannten Ansprechpartner*in bei
          den Fernleitungsnetzbetreibern. Bitte senden Sie das ausgefüllte
          Formular bis spätestens zum 16. April 2021 an den zuständigen
          Fernleitungsnetzbetreiber. Bitte keine Anfragen an den FNB Gas
          stellen.
        </p>
        <p style={styles.text}>
          <h3 style={styles.coloredHeader}>Ansprechpartner*in</h3>
          <TableContainer style={{ width: '50%' }}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell style={styles.header}>
                    Fernleitungsnetzbetreiber
                  </TableCell>
                  <TableCell style={styles.header}>
                    Ansprechpartner*in
                  </TableCell>
                  <TableCell style={styles.header}>E-Mail</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((item) => (
                  <TableRow style={{ padding: 0 }} key={index}>
                    <TableCell style={styles.names}>{item.fnb}</TableCell>
                    <TableCell style={styles.names}>{item.partner}</TableCell>
                    <TableCell style={styles.names}>{item.email}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          {/*     Fernleitungsnetzbetreiber Ansprechpartner*in E-Mail
           bayernets GmbH
          Stefanie Jacobi stefanie.jacobi@bayernets.de Ferngas Netzgesellschaft
          mbh Hagen Kropp hagen.kropp@ferngas.de Fluxys Deutschland GmbH
          Alessandro Brunoni alessandro.brunoni@fluxys.com Fluxys TENP GmbH
          Alessandro Brunoni alessandro.brunoni@fluxys.com GASCADE Gastransport
          GmbH Nils Melcher nils.melcher@gascade.de Gastransport Nord GmbH Tammo
          Dunkhase tammo.dunkhase@gtg-nord.de Gasunie Deutschland Transport
          Services GmbH Thorsten Brümmer thorsten.bruemmer@gasunie.de GRTgaz
          Deutschland GmbH Susan Valtin susan.valtin@grtgaz-deutschland.de
          Lubmin-Brandov Gastransport GmbH Marion Abel info@lbtg.de NEL
          Gastransport GmbH Nils Melcher nils.melcher@gascade.de Nowega GmbH
          Martin Jones m.jones@nowega.de ONTRAS Gastransport GmbH René Döring
          rene.doering@ontras.com OPAL Gastransport GmbH & Co. KG Nils Melcher
          nils.melcher@gascade.de Open Grid Europe GmbH Thomas Stang
          gastransport@oge.net terranets bw GmbH Anitha Prasad
          anitha.prasad@terranets-bw.de Thyssengas GmbH Jan Eisenberg
          jan.eisenberg@thyssengas.com */}
        </p>
        <p style={styles.text}>
          <h3 style={styles.coloredHeader}>
            Kontakt des Datenschutzbeauftragten
          </h3>
          bayernets GmbH datenschutz@bayernets.de Ferngas Netzgesellschaft mbH
          {<br />}
          datenschutz@ferngas.de Fluxys Deutschland GmbH{<br />}
          datenschutz.deutschland@fluxys.com Fluxys TENP GmbH{<br />}
          datenschutz.deutschland@fluxys.com GASCADE Gastransport GmbH{<br />}
          datenschutz@gascade.de Gastransport Nord GmbH datenschutz@gtg-nord.de
          {<br />}
          Gasunie Deutschland Transport Services GmbH datenschutz@gasunie.de
          {<br />}
          GRTgaz Deutschland GmbH datenschutz@grtgaz-deutschland.de{<br />}
          Lubmin-Brandov Gastransport GmbH info@lbtg.de NEL Gastransport GmbH
          {<br />}
          datenschutz@gascade.de Nowega GmbH datenschutz@nowega.de ONTRAS
          {<br />}
          Gastransport GmbH datenschutzbeauftragter@ontras.com OPAL Gastransport
          {<br />}
          GmbH & Co. KG datenschutz@gascade.de Open Grid Europe GmbH{<br />}
          oge-datenschutz@oge.net terranets bw GmbH datenschutz@terranets-bw.de
          {<br />}
          Thyssengas GmbH datenschutz@thyssengas.com
        </p>
      </div>
    );
  }
}
